import React from 'react';
import { View, Text, Modal, StyleSheet, TouchableOpacity } from 'react-native';

const Modal1 = ({ visible, onClose }) => {
  return (
    <View style={styles.container}>
    <TouchableOpacity style={styles.button} onPress={visible}>
      <Text style={styles.buttonText}>Abrir Modal</Text>
    </TouchableOpacity>

    <Modal
      animationType="slide"
      transparent={true}
      visible={visible}
      onRequestClose={handleCloseModal}
    >
      <View style={styles.modalContainer}>
        <View style={styles.modalContent}>
          <Text style={styles.modalText}>Conteúdo do modal</Text>
          <TouchableOpacity style={styles.modalButton} onPress={close}>
            <Text style={styles.modalButtonText}>Fechar</Text>
          </TouchableOpacity>
        </View>
      </View>
    </Modal>
  </View>
  );
};


const styles = StyleSheet.create({
    container: {
      flex: 1,
      alignItems: 'center',
      justifyContent: 'center',
    },
    modalContainer: {
      flex: 3,
      height: 500, 
      justifyContent: 'center',
      alignItems: 'center',
      backgroundColor: "#FFFFFF",
    },
  });
  

export default Modal1;
