import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';
import { useFonts, Inter_900Black } from '@expo-google-fonts/inter';

const Task = (props) => {
  const [circleColor, setCircleColor] = useState(props.doneAt ? '#36457D' : '#808080');

  const toggleCircleColor = () => {
    setCircleColor(circleColor === '#808080' ? '#36457D' : '#808080');
  };

  let [fontsLoaded] = useFonts({
    Inter_900Black,
  });

  if (!fontsLoaded) {
    return null;
  }

  return (
    <View style={styles.container}>
      <Text style={styles.text}>{props.desc}</Text>
      <Text>{props.pontuacao + ' pontos'}</Text>
      <TouchableOpacity onPress={toggleCircleColor}>
        <View style={[styles.circle, { backgroundColor: circleColor }]} />
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    display: 'flex',
    flexDirection: 'row',
    paddingTop: 15,
    paddingBottom: 10,
    justifyContent: 'space-between',
    alignItems: 'center',
    width: '70%',
    borderBottomWidth: 1,
    borderBottomColor: '#ccc',
  },
  text: {
    fontFamily: 'Inter_900Black',
    fontSize: 18,
    fontWeight: '700',
  },
  circle: {
    width: 20,
    height: 20,
    borderRadius: 10,
    borderWidth: 2,
  },
});

export default Task;
