import { StyleSheet, Text, View, TextInput } from 'react-native'
import React from 'react'

export default function InputText(props) {
  return (
    <View style={styles.view}>
      <Text style={styles.text}>{props.children}</Text>
      <TextInput secureTextEntry={props.secure} style={styles.input} onChange={props.onChangeText} value={props.text} placeholder={props.placeholder}></TextInput>
    </View>
  )
}

const styles = StyleSheet.create({
    text:{
        fontFamily: 'Roboto',
        fontSize: 13,
        lineHeight: '1rem',
        marginBottom: '0.3rem'
    },
    view:{
      marginBottom: '0.75rem'
    },
    input:{
      backgroundColor: "#f1f2f3",
      width: '60vw',
      border:'0',
      borderRadius: '0.5rem',
      padding: '0.4rem'
    }
})
